from scratch.deep_learning import Tensor
class Vocabulary:
def __init__(self, words: List[str] = None) -> None:
self.w2i: Dict[str, int] = {}
# mapeando word -> word_id
self.i2w: Dict[int, str] = {}
# mapeando word_id -> word
for word in (words or []):
# Si hay palabras,
self.add(word)
# las añade.
@property
def size(self) -> int:
"""how many words are in the vocabulary"""
return len(self.w2i)
def add(self, word: str) -> None:
if word not in self.w2i:
# Si la palabra es nueva:
word_id = len(self.w2i)
# Halla el siguiente id.
self.w2i[word] = word_id
# Añade al mapeado word -> word_id.
self.i2w[word_id] = word
# Añade al mapeado word_id -> word.
def get_id(self, word: str) -> int:
"""return the id of the word (or None)"""
return self.w2i.get(word)
def get_word(self, word_id: int) -> str:
"""return the word with the given id (or None)"""
return self.i2w.get(word_id)
def one_hot_encode(self, word: str) -> Tensor:
word_id = self.get_id(word)
assert word_id is not None, f"unknown word {word}"
return [1.0 if i == word_id else 0.0 for i in range(self.size)]
